import React, { useState, useRef, useEffect } from 'react';
import { claudeApi } from '../services/claudeApi';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'assistant';
  timestamp: Date;
  isLoading?: boolean;
}

const LeagueChat: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hi! I'm your league assistant. I can answer questions about your Amberwood Fantasy Football League. Try asking me about standings, rosters, trades, or draft results!",
      sender: 'assistant',
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputText.trim() || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText.trim(),
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);

    // Add loading message
    const loadingMessage: Message = {
      id: (Date.now() + 1).toString(),
      text: 'Analyzing your question and fetching league data...',
      sender: 'assistant',
      timestamp: new Date(),
      isLoading: true
    };
    setMessages(prev => [...prev, loadingMessage]);

    try {
      // For now, we'll use a simple response system
      // In a full implementation, this would integrate with the MCP server
      const response = await processLeagueQuestion(inputText.trim());

      // Replace loading message with actual response
      setMessages(prev => prev.map(msg => 
        msg.id === loadingMessage.id 
          ? { ...msg, text: response, isLoading: false }
          : msg
      ));
    } catch (error) {
      console.error('Error processing question:', error);
      setMessages(prev => prev.map(msg => 
        msg.id === loadingMessage.id 
          ? { 
              ...msg, 
              text: "Sorry, I encountered an error while processing your question. Please try again.", 
              isLoading: false 
            }
          : msg
      ));
    } finally {
      setIsLoading(false);
      inputRef.current?.focus();
    }
  };

  const processLeagueQuestion = async (question: string): Promise<string> => {
    // This is a simplified version. In a full implementation, you would:
    // 1. Analyze the question to determine what data is needed
    // 2. Call the appropriate MCP server endpoints
    // 3. Use Claude to generate a natural language response

    const lowerQuestion = question.toLowerCase();

    if (lowerQuestion.includes('standing') || lowerQuestion.includes('rank')) {
      return "Based on current league standings, here are the top teams:\n\n1. Team Alpha - 10-3 (1,247.5 PF)\n2. Team Beta - 9-4 (1,198.2 PF)\n3. Team Gamma - 9-4 (1,156.7 PF)\n\nThe playoff race is heating up with several teams fighting for the final spots!";
    }

    if (lowerQuestion.includes('roster') || lowerQuestion.includes('player')) {
      return "I can help you analyze rosters! Each team has built unique strategies:\n\n‚Ä¢ Some teams are heavy on RB depth\n‚Ä¢ Others are banking on elite WR corps\n‚Ä¢ A few teams have streaming strategies at QB\n\nWould you like me to analyze a specific team's roster or compare players?";
    }

    if (lowerQuestion.includes('draft') || lowerQuestion.includes('pick')) {
      return "Looking at this year's draft results:\n\nüèÜ Best Early Pick: Christian McCaffrey (1st overall)\nüìà Best Value Pick: Puka Nacua (Round 8)\nüò¨ Biggest Reach: [Player] went 3 rounds early\n\nThe draft strategy variety really shows in current standings!";
    }

    if (lowerQuestion.includes('trade') || lowerQuestion.includes('transaction')) {
      return "Recent league activity has been intense!\n\nüìä Most Active Trader: [Team Name] (5 trades)\nüî• Biggest Trade: [Player] for [Player] + picks\nüí∞ Waiver Wire Gems: Several managers found gold\n\nThe trade deadline really shook up several rosters!";
    }

    if (lowerQuestion.includes('matchup') || lowerQuestion.includes('schedule')) {
      return "This week's key matchups:\n\n‚ö° Battle for 1st: Team Alpha vs Team Beta\nüî• Playoff Implications: Team Gamma vs Team Delta\nüëë Must-Win Game: Team Echo needs this badly\n\nEvery game matters as we head into playoffs!";
    }

    // Default response
    return `That's an interesting question about "${question}"! I can help you with:\n\n‚Ä¢ Current standings and playoff picture\n‚Ä¢ Roster analysis and player comparisons\n‚Ä¢ Draft results and trade history\n‚Ä¢ Weekly matchups and schedules\n‚Ä¢ Player ownership and availability\n\nTry asking me something more specific, like "Who's leading the league?" or "Show me the recent trades"`;
  };

  return (
    <div className="league-chat-container">
      <div className="chat-header">
        <div className="chat-title">
          <img src="/claude-logo-png_seeklogo-554540.png" alt="Claude" className="chat-claude-logo" />
          <h2>League Assistant</h2>
        </div>
        <div className="chat-subtitle">
          Ask me anything about your fantasy league!
        </div>
      </div>

      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.sender}`}>
            <div className="message-content">
              <div className="message-text">
                {message.isLoading ? (
                  <div className="loading-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                ) : (
                  <pre>{message.text}</pre>
                )}
              </div>
              <div className="message-time">
                {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSubmit} className="chat-input-form">
        <div className="chat-input-container">
          <input
            ref={inputRef}
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            placeholder="Ask about standings, rosters, trades, or anything else..."
            className="chat-input"
            disabled={isLoading}
          />
          <button 
            type="submit" 
            className="chat-send-button"
            disabled={!inputText.trim() || isLoading}
          >
            {isLoading ? '‚è≥' : '‚û§'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default LeagueChat;
